import '../style.css';
import GameBox from '../gamebox/gamebox';

/**
   * Generates generic white box, containing a <h1> title and Content component.
   * Default width: 300, height: 600
   */
function WhiteBox({ title, Content, width, height }) {
  let boxwidth, boxheight;
  if (width === undefined) boxwidth = 300;
  else boxwidth = width;

  if (height === undefined) boxheight = 600;
  else boxheight = height;
  return (
    <div className="square" style={{ width: boxwidth, height: boxheight }}>
      <h1 className="title">{title}</h1>
      <Content />
    </div>
  );
}

export default WhiteBox;
