import '../style.css';
import GameBox from '../gamebox/gamebox';
import WhiteBox from '../whitebox/whitebox';
import games from '../../../info';
import searchById from '../../../searchById';


function Title({ id }) {
  const livetext = searchById(id).livetext;
  return (
    <div class="matchlive">
      <h1 class="title">Match</h1>
      {
        livetext.toLowerCase() === "live" ?
          <p class="livetext">LIVE</p> :
          <p class="fttext">FT</p>
      }
    </div>
  );
}

function Content({ id }) {
  return (
    <h1>My id is {id}</h1>
    // <div>
    //   {
    //     games.map(game => {
    //       return (
    //         <GameBox id={game.id} team1={game.team1} team2={game.team2} score1={game.score1} score2={game.score2} livetext={game.livetext} />
    //       );
    //     })
    //   }
    // </div>
  );
}


function MatchBox({ id }) {
  return (
    <WhiteBox Title={Title} titleProps={{ id: id }} Content={Content} contentProps={{ id: id }} width={700} height={250} />
  );
}

export default MatchBox;
